module.exports = {
  getUserById: payload => {
    return require('./getUserById')(payload, this.options)
  },
  getUserBySrl: payload => {
    return require('./getUserBySrl')(payload, this.options)
  },
  config: (opts) => {
    let existingOpts = require('../config')
    Object.assign(existingOpts, opts)
  },
  option: (opts) => {
    this.options = opts
  }
}
