/* global describe, it */
'use strict'

const chai = require('chai')
const { expect } = chai

const messageStatusCodes = require('../')

describe('message-status-codes test', () => {
  it('상태코드를 알맞게 가져오는지', () => {
    const message = messageStatusCodes.get('1026')
    expect(message).to.equal('중복 수신번호')
  })

  it('잘못된 상태코드를 넣었을 때 빈값을 가져오는지', () => {
    const message = messageStatusCodes.get('1026293')
    expect(message).to.equal(null)
  })

  it('상태코드 리스트를 제대로 가져오는지', () => {
    const list = messageStatusCodes.getAll()
    expect(list).to.be.an('object')
    expect(list['1026']).to.be.a('string')
    expect(list['1026']).to.equal('중복 수신번호')
  })
})
