'use strict'

let hasRun = false
let cache

module.exports = function init(mongoose, modelList, modelPath, cacheOptions = {}) {
  if (mongoose.version < '3.7') throw new Error('only compatible with mongoose 3.7+')
  if (hasRun) return
  hasRun = true

  let ttl = 0
  if ('ttl' in cacheOptions) {
    ttl = cacheOptions.ttl
    delete cacheOptions.ttl
  }

  let lean = true
  if ('lean' in cacheOptions) {
    lean = cacheOptions.lean
    delete cacheOptions.lean
  }

  init._cache = cache = require('./cache')(cacheOptions)

  require('./extend-query')(mongoose, modelList, modelPath, cache, ttl, lean)
}

module.exports.clearCache = function (customKey, cb = () => {}) {
  if (!customKey) return cb()
  return cache.del(customKey, cb)
}
